import React, { useState, useEffect } from 'react';
import slider from '../Assets/embassy-boulevard-featured.webp'

const data = [
  {
    title: 'Century Eden',
    location: 'Yelahanka-Doddaballapura Road, Bangalore',
    price: '₹ 62 Lakh* Onwards',
    image: 'slider'
  },
  {
    title: 'Krisumi Waterfall Residences',
    location: 'Sector 36A, Gurgaon',
    price: '₹ 310 Cr* Onwards',
    image: 'slider'
  },
  {
    title: 'Embassy Lake Terraces',
    location: 'NH-7, Hebbal, Bangalore',
    price: '₹ 6.20 Cr* Onwards',
    image: 'slider'
  },
  // Add more items as needed
];


const Projectslider = () => {
    const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) =>
        prevIndex === data.length - 1 ? 0 : prevIndex + 1
      );
    }, 3000); // Change slide every 3 seconds
    return () => clearInterval(interval);
  }, []);

  const prevSlide = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? data.length - 1 : prevIndex - 1
    );
  };

  const nextSlide = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === data.length - 1 ? 0 : prevIndex + 1
    );
  };
  return (
    <div>
      <div className="relative w-full max-w-screen-xl mx-auto mt-10">
      <h2 className="text-center text-3xl font-bold mb-6">PROJECTS</h2>
      <div className="flex overflow-hidden relative">
        {data.map((item, index) => (
          <div
            key={index}
            className={`flex-none w-full transition-transform transform ${
              index === currentIndex ? 'translate-x-0' : '-translate-x-full'
            }`}
            style={{
              transitionDuration: '500ms',
              minWidth: '100%',
              display: index === currentIndex ? 'block' : 'none'
            }}
          >
            <img src={item.image} alt={item.title} className="w-full" />
            <div className="absolute bottom-0 left-0 right-0 bg-black bg-opacity-50 p-4 text-white">
              <h3 className="text-xl">{item.title}</h3>
              <p>{item.location}</p>
              <p>{item.price}</p>
            </div>
          </div>
        ))}
      </div>
      <button
        className="absolute left-0 top-1/2 transform -translate-y-1/2 p-2 bg-gray-800 text-white"
        onClick={prevSlide}
      >
        &#10094;
      </button>
      <button
        className="absolute right-0 top-1/2 transform -translate-y-1/2 p-2 bg-gray-800 text-white"
        onClick={nextSlide}
      >
        &#10095;
      </button>
    </div>
    </div>
  )
}

export default Projectslider
