import React from 'react';
import { Link } from 'react-router-dom';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';
import { FaFacebookSquare, FaLinkedin, FaYoutube, FaInstagramSquare } from "react-icons/fa";

import bg from "../Assets/desktop-banner.jpg";

const dataArray = [
  { id: 1, date: '13 May, 2023', title: '15 Modern Balcony Decoration Ideas for Your Home in India', content: 'Discover the latest trends in balcony decoration and get inspired to transform your space.', image: 'https://placehold.co/600x400' },
];
const BlogDetail = () => {

  return (
    <div>
      <Navbar />
      <div className="developer-header">
        <img src={bg} alt="" />
        <h1 className="text-4xl font-bold text-center mb-8 text-white">
          Blog & News
        </h1>
      </div>
      <div className="flex justify-center">
      <div className="max-w-6xl">
      <div className="grid grid-cols-1 gap-8">
          {dataArray.map((data) => (
            <Link
              key={data.id}
              to={{
                pathname: `/blog/${data.id}`,
                state: { data },
              }}
              className="bg-card p-4 rounded-lg shadow-md transform hover:scale-105 transition-transform duration-300"
            >
              <img src={data.image} alt={data.title} className="w-full h-48 object-cover rounded-t-lg" />
              <div className="p-4">
                <div className="flex justify-between items-center mb-4">
                  <span className="text-muted-foreground">{data.date}</span>
                  <div className="flex items-center space-x-2">
                    <a href="#" className="text-[20px] text-[blue] hover:text-[#fead26]"><FaFacebookSquare /></a>
                    <a href="#" className="text-[20px] text-[blue] hover:text-[#fead26]"><FaLinkedin /></a>
                    <a href="#" className="text-[20px] text-red-500 hover:text-[#fead26]"><FaYoutube /></a>
                    <a href="#" className="text-[20px] text-[orange] hover:text-[#fead26]"><FaInstagramSquare /></a>
                  </div>
                </div>
                <div className="flex justify-left gap-4 items-center">
                  <span className="text-muted-foreground">20</span>
                  <button className="bg-[#fead26] text-white px-2 py-1 rounded hover:bg-secondary/80 transition-colors duration-300">Share</button>
                </div>
                <h2 className="text-xl font-semibold mb-2">{data.title}</h2>
                <p className="text-muted-foreground mb-4">{data.content}</p>
                <span className="text-primary hover:underline text-[#fead26]">READ FULL STORY</span>
              </div>
            </Link>
          ))}
        </div>
      </div>
      </div>
      <Footer />
    </div>
  );
};

export default BlogDetail;
